sys_variable_value.value:
(function execute(inputs, outputs) {
inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
	if(inputs.status_code == '201'){
    	outputs.status = "Success";
        var val = JSON.parse(inputs.response);
      	outputs.user_id = val.id.toString();
      	outputs.user_name = val.name.formatted;
    }else
       outputs = new sn_fb_wp_spoke.FacebookWorkplaceErrorHandler().userErrorHandler(inputs,outputs);     
})(inputs, outputs);

