sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);

  if(inputs.active == "true")
  	var act_active = true;
  else if(inputs.active == "false")
  	var act_active = false;

  var req_body = {}
  req_body['userName'] = inputs.email;
  req_body['name'] = { "formatted":inputs.full_name };
  req_body['active'] = act_active;
  req_body['schemas'] = ["urn:scim:schemas:core:1.0", "urn:scim:schemas:extension:enterprise:1.0"];
  if(inputs.department)
    req_body['urn:scim:schemas:extension:enterprise:1.0'] = { "department": inputs.department };
  if(inputs.timezone)
    req_body['timezone'] = inputs.timezone;
  if(inputs.language != 'other' && !inputs.other_language)
    req_body['preferredLanguage'] = inputs.language;
  else if(inputs.language == 'other' && inputs.other_language)
    req_body['preferredLanguage'] = inputs.other_language;
  else if(inputs.language && inputs.other_language)
    req_body['preferredLanguage'] = inputs.language;

  outputs.request_body =  JSON.stringify(req_body);
})(inputs, outputs);

