sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
  if (inputs.status_code == '200') {
    if(JSON.parse(inputs.response).success)
    	outputs.status = "Success";
    else{
    	outputs.status = "Error";
      	outputs.error_message = "Provided email address does not exist in Workplace by Facebook."
    }
  } else
    outputs = new sn_fb_wp_spoke.FacebookWorkplaceErrorHandler().groupErrorHandler(inputs, outputs);  
})(inputs, outputs);

