sys_variable_value.value:
(function execute(inputs, outputs) {
  var defaultFields = 'short_description,category,state,priority,assignment_group,assigned_to'; 
  var fieldsToPost = inputs.fields;
  if (gs.nil(fieldsToPost))
    fieldsToPost = defaultFields;
  inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
  outputs.message = new sn_fb_wp_spoke.FacebookWorkplaceUtils().createMessage(inputs, fieldsToPost);
  outputs.group_id = inputs.group_id;

})(inputs, outputs);

