sys_variable_value.value:
(function execute(inputs, outputs) {

	inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
	if(inputs.status_code == '200'){
    	outputs.status = "Success";
      	outputs.post_id = JSON.parse(inputs.response)["id"];
    }else
       outputs = new sn_fb_wp_spoke.FacebookWorkplaceErrorHandler().postErrorHandler(inputs,outputs);

})(inputs, outputs);

