sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
  outputs.message = inputs.message;
  outputs.group_id = inputs.group_id;
  if (inputs.link) {
    outputs.link=inputs.link;
    outputs.link_header = "link";
  }
})(inputs, outputs);

