sys_variable_value.value:
(function execute(inputs, outputs) {
  inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
  outputs.user_id = inputs.user_id; 
  var req_body = {}
  req_body['active'] = false;
  req_body['schemas'] = ["urn:scim:schemas:core:1.0"];
  outputs.request_body = JSON.stringify(req_body); 
})(inputs, outputs);

