sys_variable_value.value:
(function execute(inputs, outputs) {
inputs = new sn_fb_wp_spoke.FacebookWorkplaceUtils().trimStringInputs(inputs);
	if(inputs.status_code == '200')
    	outputs.status = "Success";
  else
       outputs = new sn_fb_wp_spoke.FacebookWorkplaceErrorHandler().userErrorHandler(inputs,outputs);    
})(inputs, outputs);

